SELECT CONCAT(apellido1, ' ',apellido2, ' ',nombre1, ' ', nombre2) AS Estudiante, nombre AS Asignatura, nota_corte1 AS 'Nota Primer Corte', nota_corte2 AS 'Nota Segundo Corte', nota_corte3 AS 'Nota Tercer Corte', ROUND((nota_corte1 * (corte1 / 100)) + (nota_corte2 * (corte2 / 100)) + (nota_corte3 * (corte3 / 100)), 2) AS Definitiva FROM registro_notas r INNER JOIN estudiantes e ON e.id = r.estudiante_id INNER JOIN horarios h ON h.id = r.horario_id INNER JOIN asignaturas a ON a.id = h.asignatura_id INNER JOIN definir_asignatura d ON d.horario_id = r.horario_id INNER JOIN forma_evaluar f ON f.definir_id = d.id ORDER BY apellido1, apellido2, nombre1, nombre2